import React from "react";
import LibraryMusicIcon from "@mui/icons-material/LibraryMusic";
import Drop from "../utils/DropZoneForAvatar";
import { gql } from "../utils/GQL";
import { Card } from "@mui/material";
import Input from "@mui/material/Input";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import CardMedia from "@mui/material/CardMedia";
import { useEffect, useState } from "react";

import { actionAboutMe } from "../redux/actions/ActionAboutMe";

const actionUploadPhoto = async (file) => {
  const uploadFile = async (file) => {
    const url = `http://player.node.ed.asmer.org.ua/upload`;
    let formData = await new FormData();
    formData.append("photo", file);
    return fetch(url, {
      method: "POST",
      headers: localStorage.authToken
        ? { Authorization: "Bearer " + localStorage.authToken }
        : {},
      body: formData,
    })
      .then((res) => res.json())
      .then((data) => {
        return data;
      });
  };
  return await Promise.all(file.map((file) => uploadFile(file))).then(
    (values) => {
      return values;
    }
  );
};

const actionAddAvatar = (avatar) => async (dispatch, getState) => {
  console.log("userInfo", getState());
  let userInfo = getState().promise.aboutMe.payload;

  const gqlQuery = `  mutation setAvatar($id: String, $avatar: ID){
        UserUpsert(user:{_id: $id, avatar:{_id: $avatar}}){
            _id login avatar{url}
        }
    }`;

  const gqlPromise = await gql(gqlQuery, {
    id: userInfo._id,
    avatar: avatar,
  });
  await gqlPromise;

  await dispatch(actionAboutMe);
};

const UploadBox = ({ defaultPhotos = [] }) => {
  const [photos, ChangePhotos] = useState(defaultPhotos);
  const [photosIds, ChangePhotosIds] = useState(defaultPhotos);

  useEffect(() => {
    console.log(photos);
  }, [photos]);

  return (
    <Card sx={{ textAlign: "left", padding: "40px", marginBottom: "20px" }}>
      <form>
        <Drop
          imageData={(avatar) => {
            ChangePhotos((prevArray) =>
              prevArray.concat(
                track.map((img) => {
                  return { url: `${URL}${img.url}` };
                })
              )
            );
            ChangePhotosIds((prevArray) =>
              prevArray.concat(
                track.map((img) => {
                  return { _id: img._id };
                })
              )
            );
          }}
          onUpload={actionUploadPhoto}
        />
        <div style={{ display: "flex" }}>
          {Array.isArray(photos)
            ? photos.map((track) => (
                <CardMedia key={track.url} track={track.url}>
                  <LibraryMusicIcon />
                </CardMedia>
              ))
            : ""}
        </div>
        <Button
          onClick={() => {
            console.log("trackIDS", photosIds[0]);
            actionAddAvatar(photosIds[0]);
          }}
        >
          <h3>ChangeAvatar</h3>
        </Button>
      </form>
    </Card>
  );
};

export const ChangeAvatarPage = () => {
  return <UploadBox />;
};
