import { useEffect, useState } from "react";
import { Card } from "@mui/material";
import Drop from "../utils/DropZoneForAvatar";
import { Button } from "@mui/material";
import uploadFile from "../utils/UploadFile";
import { CardMedia } from "@mui/material";

export const UserPage = ({
  match: {
    params: { _id },
  },
  props = {},
  posts = [],
  aboutMe,
  onLoadUserInf,
  onLoadUserPosts,
  onProfileChange,
  postLike,
  postUnlike,
  onFollow,
  onUnfollow,
}) => {
  const [avatarSrc, changeAvatarSrc] = useState("");
  const [profileImage, ChangeProfileImage] = useState();
  const [popUpDropMenu, changePopUpDropMenu] = useState(false);

  useEffect(() => {
    console.log(
      "i`m changed",
      props?.avatar?.url,
      `http://player.node.ed.asmer.org.ua/${props?.avatar?.url}`,
      avatarSrc
    );
    if (props?.avatar?.url)
      changeAvatarSrc(
        `http://player.node.ed.asmer.org.ua/${props?.avatar?.url}`
      );
  }, [props]);
  <Card
    sx={{
      position: "fixed",
      top: 200,
      margin: "auto",
      height: 400,
      zIndex: 4,
      overflow: "hidden",
      background: "rgb(231,231,231)",
    }}
  >
    <Drop
      style={{ height: "100%" }}
      imageData={(image) => {
        ChangeProfileImage(image[0]);
      }}
      onUpload={uploadFile}
    />

    <Button
      sx={{ position: "absolute", right: "0", zIndex: 5, top: 0, fontSize: 20 }}
      onClick={() => {
        changePopUpDropMenu(!popUpDropMenu);
      }}
    >
      X
    </Button>

    <div style={{ display: "flex", justifyContent: "center" }}>
      <CardMedia
        image={`http://player.node.ed.asmer.org.ua/${profileImage?.url}`}
        component="img"
        sx={{ margin: "10px", width: "55%" }}
      />
    </div>
    <Button
      variant="contained"
      onClick={async () => {
        await onProfileChange(profileImage._id, "SobakaSutulaya");
        onLoadUserInf(_id);
        changePopUpDropMenu(!popUpDropMenu);
      }}
    >
      Change Profile Page
    </Button>
  </Card>;
};
